rder-bottom: 1px solid transparent !important; box-shadow: none !important; }\r\n.btn-flat:hover { border-bottom: 1px solid !important; }\r\n.btn-modern.btn-xl { font-size: 1.4em; padding: 1.2em 2.8em; letter-spacing: -0.025em; }\r\n.btn-modern.btn-lg { font-size: 1.0714em; padding: 1em 2.5em; }\r\n.btn-modern.btn-md { font-size: .9286em; padding: .9em 2.1em; }\r\n.thumb-info-side-image { border: none; display: -ms-flexbox; display: flex; -ms-flex-align: center; align-items: center; }\r\n.thumb-info-side-image .thumb-info-side-image-wrapper { float: none; width: 225px; display: block; margin: 0; }\r\n.thumb-info-side-image .thumb-info-caption { display: block; margin-left: -2rem; position: relative; background: #fff; padding: 2rem 2rem 1.5rem 2.25rem; }\r\n.thumb-info-side-image .thumb-info-caption .thumb-info-caption-text { padding: 0; }\r\n.thumb-info-side-image h2 { font-size: 1.125rem; font-weight: 600; text-transform: uppercase; }\r\n.post-carousel .post-item .post-meta { font-size: 0.6875rem !important; font-weight: 700; letter-spacing: 0.1em; background: #222529; color: #fff; padding: 0.125rem .5rem; display: inline-block; text-transform: uppercase; }\r\n.thumb-info-caption p { font-size: .8125rem; line-height: 1.7; letter-spacing: .03em; }\r\n.post-item .read-more { text-transform: uppercase; font-size: .875rem; letter-spacing: -.025em; font-weight: 700; }\r\n@media (max-width: 480px) {\r\n    .thumb-info-side-image { display: block; }\r\n    .thumb-info-side-image .thumb-info-caption { margin-left: 0; }\r\n}\r\n\r\n/* shop */\r\nbody.woocommerce .left-sidebar, body.woocommerce .right-sidebar, body.woocommerce .main-content { padding-top: 40px; }\r\n.single-product .product-layout-transparent { margin-top: -40px; }\r\n#mini-cart .buttons a { color: #61605a; }\r\n.pagination.load-more .next { display: inline-block; width: auto; background: #222529; border: none; padding: 1.1em 2.6em; color: #fff; font-size: 0.875rem; }\r\nul.products li.product-category .thum